<script>
  import { onMount } from 'svelte';
  import { modalAction } from '../store/store';
  import { tooltip } from '../js/__Tooltip';
  import { operator } from '../store/store';
  import Avatar from './Avatar.svelte';

  const setWebAddr = () => $modalAction = 'setWebAddr';

  const setOptions = () => $modalAction = 'setOptions';

  const showHelp = () => $modalAction = 'showHelp';

  onMount(() => {
    let timerID;
    if (!$operator.site) {
      timerID = setTimeout(() => {
        $modalAction = 'setWebAddr';
      }, 5000);
    }

    return () => { if (timerID) clearTimeout(timerID); }
  });

</script>

<aside class="left_aside">
  <Avatar/>
  <nav class="profile_nav">
    <img class="p_icon" on:click={showHelp} use:tooltip title="Help..." alt="help" 
        src=" data:image/png;base64,">
    <img class="p_icon" on:click={setWebAddr} use:tooltip title="Set Web address..."
        src='data:image/png;base64,' alt='' />
    <img class="p_icon" on:click={setOptions} use:tooltip title="Set parameters..."
        src='data:image/png;base64,' alt='' />
  </nav>
</aside>

<style>
  .profile_nav {
    display: flex;
    flex-flow: column nowrap;
    align-items: center;
    margin-bottom: 1rem;
  }
</style>